import numpy as np

def score(item, bins):
    max_capacity = np.max(bins)
    scores = np.zeros_like(bins, dtype=float)

    for i, remaining_capacity in enumerate(bins):
        if remaining_capacity >= (max_capacity - item):
            scores[i] = -np.inf
        else:
            scores[i] = (max_capacity - remaining_capacity) / item

    return scores
