import numpy as np

def dpcrc_score(item, bins):
    # Initialize the scores for each bin
    scores = np.abs(bins - item)

    # Iteratively update the scores based on the previous scores and the remaining capacities of the bins
    for i in range(len(bins)):
        if bins[i] >= item:
            scores[i] = max(scores[i], scores[i-1] + item)
        else:
            scores[i] = max(scores[i], scores[i-1] + bins[i])

    return scores
